mapper.update:
update bhs_article
set views = #{views}
where aid = #{aid}

mapper.delete:
delete
from bhs_tag_relation
where aid = #{aid}
  and tid in
(select tid from bhs_tag where name not in

    #{tag.name}
)

mapper.select:
select ba.aid
from bhs_article ba
         left join bhs_tag_relation btr on btr.aid = ba.aid


        and uid = #{uid}


        and cid = #{uid}


        and tid = #{tid}


        and status = #{status}


group by ba.aid

select count(*)
from bhs_article
where cid = #{cid}

select views
from bhs_article
where aid = #{aid}
