mapper.delete:
delete
from bhs_tag_relation
where aid = #{aid}
  and tid in
(select tid from bhs_tag where name not in

    #{tag.name}
)

mapper.select:
select aid
from bhs_article


        cid = #{cid}


        and uid = #{uid}


        and status = #{status}

select count(*)
from bhs_article
where cid = #{cid}

select aid
from bhs_article
where aid = #{aid}
