mapper.update:
update bhs_user
set rid = #{role.rid}, name = #{name}, mail = #{mail}, link = #{link}
where uid = #{uid}

mapper.select:
select uid, name, password, mail, link, rid, enabled
from bhs_user
where uid = #{uid}

select uid, name, password, mail, link, rid, enabled
from bhs_user
where name = #{name}
